<?phpfunction post_data($url, $data){    $ch = curl_init();    curl_setopt($ch, CURLOPT_URL, $url);    curl_setopt($ch, CURLOPT_VERBOSE, 0);    curl_setopt($ch, CURLOPT_HEADER, 0);    curl_setopt($ch, CURLOPT_POST, true);    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);    curl_setopt($ch, CURLOPT_POSTFIELDS, $data);    $ret = curl_exec($ch);    curl_close($ch);    return $ret;}/** * Submit Request * * @param $username * @param $password * @param $imgFileName * @return mixed|null|string */function uploadcaptcha($username, $password, $imgFileName){    $fp = fopen($imgFileName, "rb");    if (!$fp) return null;    $fileSize = filesize($imgFileName);    if ($fileSize <= 0) return null;    $data = fread($fp, $fileSize);    fclose($fp);    $enc_data = base64_encode($data);    $data = post_data("http://captchatypers.com/Forms/UploadFileAndGetTextNEW.ashx", array("action" => "UPLOADCAPTCHA", "username" => $username, "password" => $password, "file" => $enc_data, "chkCase" => "1"));    return $data;}/** * Set as Bad Image * * @param $username * @param $password * @param $imageId * @return mixed */function badImage($username, $password, $imageId){    $data = post_data("http://captchatypers.com/Forms/SetBadImage.ashx", array("action" => "SetBadImage", "username" => $username, "password" => $password, "imageid" => $imageId));    return $data;}/** * Check Balance * * @param $username * @param $password * @return mixed */function checkBalance($username, $password){    $data = post_data("http://captchatypers.com/Forms/RequestBalance.ashx", array("action" => "REQUESTBALANCE", "username" => $username, "password" => $password));    return $data;}?>